import React, { useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux"
import { Link, Redirect } from "react-router-dom";
import * as Yup from "yup";
import { Field, Formik } from "formik";
import { faLock, faUser } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  BackgroundContainer,
  ButtonContainer,
  Container,
  StyledButton,
  StyledCard,
  StyledForm,
  StyledInput,
  StyledLink,
  StyledNotification,
  Title
} from "./styles";
import Navbar from '../Navbar/Navbar';
import { resetRegisterState } from './registerSlice';
import { createBrowserHistory } from "history";

const RegisterForm = () => {
  const registerState = useSelector(state => state.registerState);
  const dispatch = useDispatch();

  useEffect(() => {
    if ("usertoken" in localStorage) {
      createBrowserHistory().push("/home");
    }
    dispatch(resetRegisterState());
  }, [dispatch])

  const initialValues = {
    email: "",
    password: "",
  }

  const validateSchema = Yup.object({
    email: Yup.string().email("Invalid email format").required("Email is required"),
    name: Yup.string().required("Name is required"),
    password: Yup.string().required("Password is required"),
    passwordConfirmation: Yup.string().test(("password-match", "Passwords must match", (value) => {
      return this.parent.password === value;
    }))
  })
  return (
    <div>
      <Navbar />
      <BackgroundContainer>
        <Container>
          <StyledCard>
            {registerState.error && (
              <StyledNotification title={registerState.error.message} hideCloseButton={true} icon="error" />
            )}
            {registerState.isRegisterSuccess && (
              <StyledNotification title="Register successful. Redirecting..." hideCloseButton={true} icon="success" />
            )}
            <Title>Sign Up For Bloggr</Title>
            <Formik initialValues={initialValues} validationSchema={validateSchema} onSubmit={onSubmit}>
              {({ errors, touched }) => (
                <StyledForm>
                  <Field
                    name="email"
                    label="Email"
                    as={StyledInput}
                    icon={<FontAwesomeIcon icon={faUser} />}
                    error={errors.email && touched.email ? errors.email : null}
                    placeholder="Enter email"
                  />
                  <Field
                    name="password"
                    label="Password"
                    as={StyledInput}
                    icon={<FontAwesomeIcon icon={faLock} />}
                    type="password"
                    error={errors.password && touched.password ? errors.password : null}
                    placeholder="Enter password"
                  />
                  <Field
                    name="passwordConfirmation"
                    label="Password"
                    as={StyledInput}
                    icon={<FontAwesomeIcon icon={faLock} />}
                    type="password"
                    error={errors.password && touched.password ? errors.password : null}
                    placeholder="Enter password"
                  />
                  <ButtonContainer>
                    <StyledButton type="submit" label="Sign In" variant="brand" disabled={registerState.isLoading} />
                    <StyledLink to="/register">
                      <StyledButton type="submit" label="Register" variant="neutral" />
                    </StyledLink>
                  </ButtonContainer>
                </StyledForm>
              )}
            </Formik>
          </StyledCard>
        </Container>
      </BackgroundContainer>
    </div>
  );
}

export default RegisterForm;
