import React, { useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux"
import { Redirect } from "react-router-dom";
import useStyles from "./styles";
import * as Yup from "yup";
import { Field, Formik, Form } from "formik";
import { faLock, faUser } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
	BackgroundContainer,
	ButtonContainer,
	Container,
	NotificationContainer,
	ResendButton,
	StyledButton,
	StyledCard,
	StyledForm,
	StyledInput,
	StyledLink,
	StyledNotification,
	Title
} from "./styles";




const LoginForm = () => {
	// const classes = useStyles();
	const loginState = useSelector(state => state.loginState);
	const dispatch = useDispatch();

	if ("usertoken" in localStorage) {
		return <Redirect to="/home" />;
	}
	// useEffect(() => {
	//     dispatch(resetLoginState());
	//   }, [dispatch]);    
	const initialValues = {
		email: "",
		password: "",
	}

	const validateSchema = Yup.object({
		email: Yup.lazy((value) =>
			/\@/.test(value)
				? Yup.string().email("Invalid email").required("This field is required")
				: Yup.string().required("This field is required")
		),
		password: Yup.string().required("Password is required")
	});
	return (
		<BackgroundContainer>
			<Container>
				<Title>Sign in</Title>

				<StyledCard>
					{/* {loginState.error && loginState.error.type !== LoginError.EmailIsNotConfirmed && (
						<StyledNotification title={loginState.error.message} hideCloseButton={true} icon="error" />
					)}
					{loginState.isLoginSuccess && (
						<StyledNotification title="Login successful. Redirecting..." hideCloseButton={true} icon="success" />
					)}
					{resendEmailSection} */}

					<Formik initialValues={initialValues} validationSchema={validateSchema} onSubmit={onSubmit}>
						{({ errors, touched }) => (
							<StyledForm>
								<Field
									name="email"
									label="Email"
									as={StyledInput}
									icon={<FontAwesomeIcon icon={faUser} />}
									error={errors.email && touched.email ? errors.email : null}
									placeholder="Enter email"
								/>
								<Field
									name="password"
									label="Password"
									as={StyledInput}
									icon={<FontAwesomeIcon icon={faLock} />}
									type="password"
									error={errors.password && touched.password ? errors.password : null}
									placeholder="Enter password"
								/>
								<ButtonContainer>
									<StyledButton type="submit" label="Sign In" variant="brand" disabled={loginState.isLoading} />
								</ButtonContainer>
							</StyledForm>
						)}
					</Formik>
				</StyledCard>
			</Container>
		</BackgroundContainer>
	);
}

export default LoginForm;
